// frontend/src/pages/ProjectManagerAddTaskPage.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import API from '../services/api';
import FileTree from '../components/FileTree';
import '../styles/ProjectManagerTask.css';

function ProjectManagerAddTaskPage() {
    const { projectId } = useParams();
    const navigate = useNavigate();

    // Basic Task form state
    const [annotators, setAnnotators] = useState([]); // to populate dropdown
    const [assignedTo, setAssignedTo] = useState('');
    const [deadline, setDeadline] = useState('');
    const [instructions, setInstructions] = useState('');

    // For file selection
    const [projectTree, setProjectTree] = useState([]);
    const [selectedPaths, setSelectedPaths] = useState(new Set());
    // storing relative paths

    // On mount, fetch possible annotators and fetch folder tree
    useEffect(() => {
        fetchAnnotators();
        fetchProjectFileTree();
    }, []);

    async function fetchAnnotators() {
        try {
            // Suppose you have an endpoint /auth/users?role=Annotator
            // or you can fetch all users and filter. Adjust as needed.
            const res = await API.get('/auth/users?role=Annotator');
            setAnnotators(res.data.users || []);
        } catch (err) {
            console.error('[AddTask] fetchAnnotators error:', err);
        }
    }

    async function fetchProjectFileTree() {
        try {
            // Reuse the existing code for reading the entire folder under projectId
            // or just your `/requests/:requestId/files-tree` if that's how you do it
            const res = await API.get(`/auth/projects/${projectId}/files-tree`);
            setProjectTree(res.data.tree || []);
        } catch (err) {
            console.error('[AddTask] fetchProjectFileTree error:', err);
        }
    }

    // 1) Create the new task in DB
    async function handleCreateTask() {
        try {
            // Create the new task
            const body = {
                assigned_to: assignedTo || null,
                deadline: deadline || null,
                instructions
            };
            const res = await API.post(`/auth/projects/${projectId}/tasks`, body);
            const newTaskId = res.data.taskId;

            // 2) If we selected files/folders, copy them into the task folder
            if (selectedPaths.size > 0) {
                await API.post(`/auth/projects/${projectId}/tasks/${newTaskId}/files`, {
                    selectedPaths: Array.from(selectedPaths)
                });
            }

            // Return to Project Details
            navigate(`/pm/projects/${projectId}`);
        } catch (err) {
            console.error('[AddTask] handleCreateTask error:', err);
            alert('Failed to create task.');
        }
    }

    // Collapsible tree with checkboxes
    // We'll create a custom component below to handle color-coding or checkboxes
    return (
        <div className="pm-add-task-container">
            <h2>Create New Task for Project #{projectId}</h2>

            <div className="task-form-section">
                <label>Assign to Annotator:</label>
                <select
                    value={assignedTo}
                    onChange={(e) => setAssignedTo(e.target.value)}
                >
                    <option value="">--Unassigned--</option>
                    {annotators.map((a) => (
                        <option key={a.id} value={a.id}>{a.name}</option>
                    ))}
                </select>

                <label>Deadline:</label>
                <input
                    type="date"
                    value={deadline}
                    onChange={(e) => setDeadline(e.target.value)}
                />

                <label>Instructions:</label>
                <textarea
                    value={instructions}
                    onChange={(e) => setInstructions(e.target.value)}
                />
            </div>

            <div className="task-files-section">
                <h4>Select Folders/Files for this Task</h4>
                <CollapsibleTreeWithCheckbox
                    treeData={projectTree}
                    selectedPaths={selectedPaths}
                    setSelectedPaths={setSelectedPaths}
                />
            </div>

            <button onClick={handleCreateTask}>Create Task</button>
            <button onClick={() => navigate(`/pm/projects/${projectId}`)}>Cancel</button>
        </div>
    );
}


/**
 * Example of a collapsible tree with checkboxes:
 */
function CollapsibleTreeWithCheckbox({ treeData, selectedPaths, setSelectedPaths }) {
    if (!treeData || treeData.length === 0) {
        return <p>No files found.</p>;
    }

    return (
        <ul className="file-tree">
            {treeData.map((node, idx) => (
                <TreeCheckboxNode
                    key={idx}
                    node={node}
                    selectedPaths={selectedPaths}
                    setSelectedPaths={setSelectedPaths}
                />
            ))}
        </ul>
    );
}

function TreeCheckboxNode({ node, selectedPaths, setSelectedPaths }) {
    const [isOpen, setIsOpen] = useState(false);

    const isChecked = selectedPaths.has(node.path);

    const toggleCheckbox = () => {
        // If checked, remove from set. Otherwise, add to set
        const newSet = new Set(selectedPaths);
        if (newSet.has(node.path)) {
            newSet.delete(node.path);
        } else {
            newSet.add(node.path);
        }
        setSelectedPaths(newSet);
    };

    // Helper to recursively select/deselect children if you want that behavior
    const selectAllChildren = (childNode, selected) => {
        // If folder, select all children
        if (childNode.children) {
            childNode.children.forEach((c) => {
                selectAllChildren(c, selected);
            });
        }
        if (selected) {
            selectedPaths.add(childNode.path);
        } else {
            selectedPaths.delete(childNode.path);
        }
    };

    // This is purely a basic example. You can expand it to auto-select children, color code, etc.
    return (
        <li>
            {node.type === 'directory' ? (
                <>
                    <div
                        role="button"
                        tabIndex={0}
                        className="folder-toggle"
                        onClick={() => setIsOpen(!isOpen)}
                        onKeyDown={(e) => e.key === 'Enter' && setIsOpen(!isOpen)}
                    >
                        <span className="folder-arrow">{isOpen ? '‚åÑ' : '>'}</span>
                        <input
                            type="checkbox"
                            checked={isChecked}
                            onChange={(e) => {
                                toggleCheckbox();
                                // optionally, if you want to select all children automatically:
                                selectAllChildren(node, e.target.checked);
                                setSelectedPaths(new Set(selectedPaths));
                            }}
                            onClick={(e) => e.stopPropagation()} // so we don't also toggle open on checkbox
                        />
                        <span className="folder-icon">üìÅ</span> {node.name}
                    </div>
                    {isOpen && node.children && (
                        <ul>
                            {node.children.map((child, i) => (
                                <TreeCheckboxNode
                                    key={i}
                                    node={child}
                                    selectedPaths={selectedPaths}
                                    setSelectedPaths={setSelectedPaths}
                                />
                            ))}
                        </ul>
                    )}
                </>
            ) : (
                // It's a file
                <div className="file-item">
                    <input
                        type="checkbox"
                        checked={isChecked}
                        onChange={(e) => {
                            toggleCheckbox();
                        }}
                    />
                    <span className="file-icon">üìÑ</span> {node.name}
                </div>
            )}
        </li>
    );
}

export default ProjectManagerAddTaskPage;
